import {CHANGE_CURRENT_CHAT} from "constants/action-types";

export const REGISTER = 'REGISTER';
export const LOGIN = 'LOGIN';

export const isLoading = type => ({type: `${type}_LOAD`});
export const isSuccess = type => ({type: `${type}_SUCCESS`});
export const isFailure = type => ({type: `${type}_FAILURE`});
export const changeCurrentChat = chat_id => {
    return {type: CHANGE_CURRENT_CHAT, payload: chat_id}
}