const isLoading = type => ({type: `${type}_LOAD`});
const isSuccess = (type, payload = {}) => ({type: `${type}_SUCCESS`, payload});
const isFailure = type => ({type: `${type}_FAILURE`});


export {isLoading, isSuccess, isFailure}

function action(type, payload = {}) {
    return {type, payload}
}

//ChatPage
export const GetChatList = payload => action('GET_CHAT_LIST', payload);
export const ClearChatList = () => action('CLEAR_CHAT_LIST'); 
//MessagePage
export const GetMessageList = payload => action('GET_MESSAGE_LIST', payload);
export const ReceiveMessage = payload => action('RECEIVE_MESSAGE', payload);
export const ClearMessageList = () => action('CLEAR_MESSAGE_LIST');
//Router
export const GetUserID = payload => action('LOGIN_USER_ID', payload);
