const isLoading = type => ({type: `${type}_LOAD`});
const isSuccess = type => ({type: `${type}_SUCCESS`});
const isFailure = type => ({type: `${type}_FAILURE`});


export {isLoading, isSuccess, isFailure}

function action(type, payload) {
    return {type, payload}
}

export const GetChatList = payload => action('GET_CHAT_LIST', payload);
export const GetMessageList = payload => action('GET_MESSAGE_LIST', payload);