import React, {Component} from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router'
import '../../styles/chat.css'
import {AllChats} from './AllChats'
import axios from 'axios'
import {Chat} from "./Chat";
import {API_URL} from "constants/server";

export class ChatWrapper extends Component {
    constructor(props) {
        super(props);
        this.state = {
            chats: [],
        };
    }

    componentDidMount = () => {
        const token = localStorage.getItem('token');
        const config = {headers: {'Authorization': 'JWT ' + token}};
        axios.get(`${API_URL}/private-chats/`, config)
            .then((response) => {
                    this.setState({'chats': response.data})
                }
            )
    };



    render() {
        const current_chat_id = this.props.current_chat_id;
        return <div className="chat-display">
            < AllChats chats={this.state.chats} ChooseChat={this.ChooseChat}/>

            {current_chat_id && <Chat key={current_chat_id} chat_id={current_chat_id}/>}
        </div>
    }
}

const mapStateToProps = state => {
    return {current_chat_id: state.chat.current_chat_id};
};



ChatWrapper = connect(mapStateToProps)(ChatWrapper);
export default ChatWrapper