export const validateRegister = values => {
    const errors = {};
    if (!values.email) {
        errors.email = 'This field needs to be filled.';
    }
    if (!values.username) {
        errors.username = 'This field needs to be filled.';
    }
    if (!values.password) {
        errors.password = 'This field needs to be filled.';
    }
    if (!values['confirm-password']) {
        errors['confirm-password'] = 'This field needs to be filled.';
    } else if (values.password !== values['confirm-password']) {
        errors['confirm-password'] = 'Confirm pass is not the same as pass.';
    }
    return errors;
};

export const validateLogin = values => {
    const errors = {};
    if (!values.email) {
        errors.email = 'This field needs to be filled.';
    }
    if (!values.password) {
        errors.password = 'This field needs to be filled.';
    }
    return errors;
};
