import React from 'react';
import { Link } from 'react-router-dom';

export default function NavigateMenu(props) {
    let { pathname } = window.location 
    return (
        <div className="container-contact100">
            <div className="wrap-contact100">
                <div className='contact100-form'>
                    <div className='tabs flex-row'>
                        <Link
                            to="/login"
                            className={
                                `tab contact100-form-title
                                ${pathname == '/login'? 'active': ''}`
                            }
                        >
                            Sign In
                        </Link>
                        <Link
                            to="/register" 
                            className={
                                `tab contact100-form-title
                                ${pathname == '/register'? 'active' : ''}`
                            }
                        >
                            Sign Up
                        </Link>
                    </div>
                    {props.children}
                </div>
                <div 
                    className="contact100-more flex-col-c-m" 
                    style={{background: "linear-gradient(120grad, #643986, #98aed5)"}}
                />
            </div>
        </div>
    ) 
}

