import React from 'react';
import { MessageList, Input, Button } from 'react-chat-elements'

export default function List({messageList, renderMessageList, sendMessage}) {
    return (
        <div>
            <MessageList
                className='message-list'
                lockable={true}
                toBottomHeight={'50%'}
                dataSource={renderMessageList(messageList)} 
            />
            <Input
                placeholder="Type here..."
                defaultValue=""
                multiline={true}
                rightButtons={
                    <Button
                        text='Send'
                        onClick={(e) => {
                            sendMessage(e.target.value)
                            e.target.value = "";
                        }}
                    />
                }
                onKeyPress={(e) => {
                    if (e.shiftKey && e.charCode === 13) {
                        return true;
                    }
                    if (e.charCode === 13) {
                        sendMessage(e.target.value)
                        e.target.value = "";
                        e.preventDefault();
                        return false;
                    }
                }}
            />

        </div>
    )
}