import React from 'react';
import { Field, reduxForm } from 'redux-form';

import { validateRegister as validate } from '../FormPatterns/validation';
import { renderField } from '../FormPatterns/renderField';

const Form = props => {
    const { handleSubmit , submitting, error, formSubmit } = props;
    return (
        <form onSubmit={handleSubmit(formSubmit)}>
            <Field 
                type="email"
                name='email'
                component={renderField}
                className="input100"
                label="Email"
            />
            <Field 
                type="text"
                name='username'
                component={renderField}
                className="input100"
                label="Username"
            />
            <Field type="text" name='first-name' component={renderField} className='input100' label="First name"/>
            <Field type="text" name='last-name' component={renderField} className='input100' label="Last name"/>
            <Field type="password" name='password' component={renderField} className='input100' label="Password"/>
            <Field type="password" name='confirm-password' component={renderField} className='input100' label="Confirm password"/>
            <div className="wrap-input100">
                <button type="button" disabled={submitting} className="contact100-form-btn">Submit</button>
            </div>
        </form>
    );
};

export default reduxForm({
    form: 'RegForm',
    validate
})(Form);
