import {CHANGE_CURRENT_CHAT} from 'constants/action-types'

const initialState = {
    current_chat: null
};

export default function chat(state = initialState, action) {
    console.log('reduce');
    switch (action.type) {
        case CHANGE_CURRENT_CHAT:
            console.log('sdsd')
            return {...state, current_chat_id: action.payload};
        default:
            return state;
    }
}