import { user } from '../constants';

const initialState = { isLoggedIn: false };

export default (state = initialState, action) => {
    switch (action.type) {
        case user.LOGIN_SUCCESS:
            return {
                ...state,
                isLoggedIn: true,
                token: action.payload.token
            };
        case user.LOGIN_USER_ID:
            return {
                ...state,
                id: action.payload.id
            }
        case user.LOGOUT:
            return {
                isLoggedIn: false,
            };
        default:
            return state;
    }
}
