import { userConstants } from '../constants';

const initialState = { isLoggedIn: false };

export default (state = initialState, action) => {
    switch (action.type) {
        case userConstants.LOGIN_SUCCESS:
            return {
                isLoggedIn: true,
            };
        case userConstants.LOGOUT:
            return {isLoggedIn: false};
        default:
            return state;
    }
}
