import { message } from '../constants';

export default (state = [], action) => {
    switch (action.type) {
        case message.GET_MESSAGE_LIST: 
            return [...action.payload]
        case message.GET_NEW_MESSAGE:
            return [...state, ...action.payload]
        case message.SEND_MESSAGE:
            return [...state, ...action.payload]
        default:
            return state;
    }
};