import { message } from '../constants';

export default (state = {fetched: false, list: []}, action) => {
    switch (action.type) {
        case message.GET_MESSAGE_LIST: 
            return {...state, fetched: true, list: [...action.payload]}
        case message.RECEIVE_MESSAGE:
            return {...state, list: [...action.payload]}
        case message.CLEAR_MESSAGE_LIST:
            return {...state, fetched: false}
        default:
            return state;
    }
};