import React from 'react';

export default function Curry(Fn) {
    const length = Fn.length;
    return function Fn2(...args) {
        if ( args.length >= length){
            let New_FN =  Fn(...args);
            return New_FN;
        } else {
            return function Fn3(...more_args){
                let new_args = args.concat(more_args);
                Fn2(...new_args);
            }
        }
    }
}