import localStorageApi from "./localStorageApi";
import axios from "axios";
const headers = {
    'Authorization': `JWT ${localStorageApi.getItem()}`
}
export default class GetLoggedData {
    static GetRequest(url) {
        return axios({
            method: 'GET',
            url,
            headers: {...headers}
        })
    }
    static PostRequest(url, data) {
        return axios({
            method: 'POST',
            url,
            data,
            headers
        })
    }
    static PutRequest() {

    }
    static DeleteRequest() {

    }
}