import localStorageApi from "./localStorageApi";
import axios from "axios";
const headers = {
    'Authorization': `JWT ${localStorageApi.getItem()}`
}
export default class GetLoggedData {
    static Get(url) {
        return axios({
            method: 'GET',
            url,
            headers: {...headers}
        })
    }
    static Post(url, data) {
        return axios({
            method: 'POST',
            url,
            data,
            headers
        })
    }
    static Put() {

    }
    static Delete() {

    }
}