import localStorageApi from "./localStorageApi";
import axios from "axios";

export default class GetLoggedData {
    static headers() {
        return {
            'Authorization': `JWT ${localStorageApi.getItem()}` 
        }
    }
    static Get(url) {
        return axios({
            method: 'GET',
            url: `/api/${url}`,
            headers: this.headers()
        })
    }
    static Post(url, data) {
        return axios({
            method: 'POST',
            url: `/api/${url}`,
            data,
            headers: this.headers()
        })
    }
    static Put() {

    }
    static Delete() {

    }
}